import 'package:flutter/material.dart';
class PosterTile extends StatelessWidget{ final String? imageUrl; final int rank; final VoidCallback? onTap; final String? semanticsLabel; const PosterTile({super.key, required this.imageUrl, required this.rank, this.onTap, this.semanticsLabel}); @override Widget build(BuildContext c){ final r=BorderRadius.circular(12); return Column(mainAxisSize: MainAxisSize.min, children:[ AspectRatio(aspectRatio:2/3, child: Semantics(label:semanticsLabel, button:true, child: InkWell(borderRadius:r, onTap:onTap, child: Ink(decoration: BoxDecoration(borderRadius:r, color: Theme.of(c).colorScheme.surfaceContainerHighest), child: ClipRRect(borderRadius:r, child: imageUrl!=null?Image.network(imageUrl!, fit:BoxFit.cover):const Icon(Icons.image, size:40)))))), const SizedBox(height:6), Text('$rank', style: Theme.of(c).textTheme.titleSmall) ]); } }
