import 'package:flutter/material.dart'; import 'package:go_router/go_router.dart';
class HomeShell extends StatefulWidget{ const HomeShell({super.key, required this.child}); final Widget child; @override State<HomeShell> createState()=>_S(); }
class _S extends State<HomeShell>{ int i=0; final r=['/','/search','/lists','/watchlist','/diary','/profile']; @override Widget build(BuildContext c)=>Scaffold(body: SafeArea(child: widget.child), bottomNavigationBar: NavigationBar(selectedIndex: i, onDestinationSelected:(v){ setState(()=>i=v); c.go(r[v]); }, destinations: const [ NavigationDestination(icon: Icon(Icons.home_outlined), selectedIcon: Icon(Icons.home), label:'Home'), NavigationDestination(icon: Icon(Icons.search), label:'Search'), NavigationDestination(icon: Icon(Icons.list_alt), label:'Lists'), NavigationDestination(icon: Icon(Icons.bookmark_border), selectedIcon: Icon(Icons.bookmark), label:'Watchlist'), NavigationDestination(icon: Icon(Icons.rate_review_outlined), selectedIcon: Icon(Icons.rate_review), label:'Diary'), NavigationDestination(icon: Icon(Icons.person_outline), selectedIcon: Icon(Icons.person), label:'Profile'), ])); }
